const socialLinks=[{name:"GitHub",url:"https://github.com/andrewboerema",iconKey:"github"},{name:"LinkedIn",url:"https://linkedin.com/in/boerema",iconKey:"linkedin"},{name:"Instagram",url:"https://instagram.com/andrewboerema",iconKey:"instagram"}];function generateSocialLinks(){const socialLinksContainer=document.querySelector(".social-links");socialLinksContainer.innerHTML="";socialLinks.forEach((link=>{const button=document.createElement("a");button.href=link.url;button.className=`social-link ${link.name.toLowerCase()}`;button.target="_blank";button.rel="noopener noreferrer";button.setAttribute("aria-label",`Visit my ${link.name} profile`);const iconContainer=document.createElement("span");iconContainer.className="icon-container";iconContainer.innerHTML=icons[link.iconKey];iconContainer.setAttribute("aria-hidden","true");const text=document.createElement("span");text.textContent=link.name;button.appendChild(iconContainer);button.appendChild(text);socialLinksContainer.appendChild(button)}))}document.addEventListener("DOMContentLoaded",generateSocialLinks);